Ext.define('dream.view.tpop.tpop1004ViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.tpop.tpop1004',

    onGridpanelAfterRender_tpop1004_car_grid: function(component, eOpts) {
        const store = component.getStore();

        store.getProxy().setUrl(dream.util.Common.BASE_URL+'/tpop/car/search');

        store.getProxy().setExtraParams({
            userCetCd: dream.util.Common.LOGIN_USER_CET_CD
        });

        store.reload();
    },

    onGridpanelItemDblClick_tpop1004_car_grid: function(dataview, record, item, index, e, eOpts) {
        const view = this.getView();
        view.fireEvent('selectUser', view, record);
        view.close();

    },

    onTpop1004_keywordAfterRender: function(component, eOpts) {
        component.on('specialkey', function(component, e) {
            if (e.getKey() === e.ENTER) {
                const button = Ext.getCmp('tpop1004_search_button');
                if (button) {
                    button.fireEvent('click', button);
                }
            }
        });
    },

    onTpop1004_search_buttonClick: function(button, e, eOpts) {
        const view = this.getView();
        const store = view.lookupReference('tpop1004_car_grid').getStore();
        const keyword = Ext.getCmp('tpop1004_keyword').getValue();

        store.getProxy().setExtraParams({
            keyword: keyword,
            userCetCd: dream.util.Common.LOGIN_USER_CET_CD

        });

        store.reload();
    },

    onTpop1004_confirm_buttonClick: function(button, e, eOpts) {
        const view = this.getView();
        const grid = view.lookupReference('tpop1004_car_grid');
        const selection = grid.getSelectionModel().getSelection();

        if (selection.length === 0) {
            Ext.Msg.alert('알림', '차량을 선택해주세요.');
            return;
        }

        const record = selection[0];


        view.fireEvent('selectUser', view, record);

        view.close();
    },

    onTpop1004_cancel_buttonClick: function(button, e, eOpts) {
        button.up('window').close();
    }

});
