    onGridpanelAfterRender_tbas1004_publish_grid: function(component, eOpts) {
        const store = component.getStore();

        // 1. proxy의 URL을 동적으로 설정
        store.getProxy().setUrl(dream.util.Common.BASE_URL+'/tbas/1004/publish-list');

        // 2. 필요 시 추가 파라미터 설정 (예: userCetCd 등)
        store.getProxy().setExtraParams({
            userCetCd: dream.util.Common.LOGIN_USER_CET_CD
        });

        // 3. 스토어 리로드
        store.reload();
    },

    onGridpanelSelectionChange_tbas1004_publish_grid: function(model, selected, eOpts) {
        if (selected.length === 0) return;

        const chulpan_sujum_grid = this.lookupReference('tbas1004_chulpan_sujum_grid');
        const chulpan_sujum_store = chulpan_sujum_grid.getStore();
        const chulpan_cd = selected[0].get('custCd');

        if (chulpan_cd) {
            chulpan_sujum_store.getProxy().setExtraParams({
                userCetCd: dream.util.Common.LOGIN_USER_CET_CD,
                chulpanCd: chulpan_cd
            });


            // store reload + 필드 변환 처리
            chulpan_sujum_store.load({
                callback: function(records, operation, success) {
                    if (success) {
                        records.forEach(function(record) {
                            ['chulpanPrtYn', 'banpNotYn'].forEach(function(field) {
                                const val = record.get(field);
                                record.set(field, val === '1' || val === 1);
                            });
                        });
                    }
                }
            });
        }

    },

