    onButtonClick_tbas1004_add_button: function(button, e, eOpts) {
        const view = this.getView();
        const publish_grid = this.lookupReference('tbas1004_publish_grid');
        const publish_selected = publish_grid.getSelection();
        if (publish_selected.length === 0) {
            Ext.Msg.alert('알림', '출판사를 선택하세요.');
            return;
        }

        view.selectedChulpanCd = publish_selected[0].get('custCd');

        const chulpan_sujum_grid = this.lookupReference('tbas1004_chulpan_sujum_grid');
        const sujum_grid = this.lookupReference('tbas1004_sujum_grid');
        const chulpan_sujum_store = chulpan_sujum_grid.getStore();
        const plugin = chulpan_sujum_grid.findPlugin('cellediting');

        const selected = sujum_grid.getSelectionModel().getSelection();

        if (selected.length === 0) {
            Ext.Msg.alert('알림', '추가할 서점을 선택하세요.');
            return;
        }

        selected.forEach((record) => {
            const alreadyExists = chulpan_sujum_store.findRecord('sujumCd', record.get('custCd'));
            if (!alreadyExists) {
                const newRec = chulpan_sujum_store.add({
                    userCetCd: dream.util.Common.LOGIN_USER_CET_CD,
                    chk: '0',
                    sujumCd: record.get('custCd'),
                    custAbbrNm: record.get('custAbbrNm'),
                    jiyukNm: record.get('jiyukNm'),
                    basicDangaRt: '',
                    singanQty: '',
                    chulpanPrtYn: true,
                    chulpanPrtGb: '3',
                    sujumPrtGb: '2',
                    banpNotYn: false
                })[0];

                // 첫 행부터 편집 가능
                plugin.startEdit(newRec, 4); // 4번째 컬럼부터 편집 시작
            }
        });


    },

    onButtonClick_tbas1004_insert_button: function(button, e, eOpts) {
        const view = this.getView();
        const chulpan_sujum_grid = this.lookupReference('tbas1004_chulpan_sujum_grid');
        const store = chulpan_sujum_grid.getStore();
        const newRecords = store.getNewRecords();
        const chulpanCd = view.selectedChulpanCd;

        if (newRecords.length === 0) {
            Ext.Msg.alert('알림', '추가된 행이 없습니다.');
            return;
        }

        // 저장 확인 팝업
        Ext.Msg.confirm('확인', '저장하시겠습니까?', function(choice) {
            if (choice !== 'yes') return;  // 취소 시 중단

            const payload = newRecords.map(r => {
                const data = r.getData();
                data.chulpanCd = chulpanCd;
                data.insertId = dream.util.Common.LOGIN_USER;

                ['chulpanPrtYn', 'banpNotYn'].forEach(field => {
                    data[field] = data[field] === true ? '1' :
                    data[field] === false ? '0' : data[field];
                });

                return data;
            });

            Ext.Ajax.request({
                url: dream.util.Common.BASE_URL + '/tbas/1004/insert',
                method: 'POST',
                jsonData: payload,
                success: function() {
                    Ext.Msg.alert('성공', '저장되었습니다.');
                    store.commitChanges();
                },
                failure: function() {
                    Ext.Msg.alert('오류', '저장 실패');
                }
            });
        });
    },

    onButtonClick_update_button: function(button, e, eOpts) {
        const grid = this.lookupReference('tbas1004_chulpan_sujum_grid');
        const store = grid.getStore();
        const updatedRecords = store.getUpdatedRecords();

        if (updatedRecords.length === 0) {
            Ext.Msg.alert('알림', '수정된 항목이 없습니다.');
            return;
        }

        Ext.Msg.confirm('확인', '수정된 데이터를 저장하시겠습니까?', function(choice) {
            if (choice !== 'yes') return;

            const payload = updatedRecords.map(r => {
                const data = r.getData();
                data.updateId = dream.util.Common.LOGIN_USER;
                data.userCetCd = dream.util.Common.LOGIN_USER_CET_CD;
                ['chulpanPrtYn', 'banpNotYn'].forEach(field => {
                    data[field] = data[field] === true ? '1' :
                    data[field] === false ? '0' : data[field];
                });
                return data;
            });

            Ext.Ajax.request({
                url: dream.util.Common.BASE_URL + '/tbas/1004/update',
                method: 'POST',
                jsonData: payload,
                success: function() {
                    Ext.Msg.alert('성공', '수정되었습니다.');
                    store.commitChanges();  // 변경 상태 초기화
                },
                failure: function() {
                    Ext.Msg.alert('오류', '수정 실패');
                }
            });
        });
    },

    onButtonClick_delete_button: function(button, e, eOpts) {
        const grid = this.lookupReference('tbas1004_chulpan_sujum_grid');
        const store = grid.getStore();
        const selection = grid.getSelectionModel().getSelection();

        if (selection.length === 0) {
            Ext.Msg.alert('알림', '삭제할 행을 선택하세요.');
            return;
        }

        Ext.Msg.confirm('확인', '선택한 항목을 삭제하시겠습니까?', function(choice) {
            if (choice !== 'yes') return;

            // 서버 전송용 데이터 구성
            const payload = selection.map(r => ({
                userCetCd: dream.util.Common.LOGIN_USER_CET_CD,
                chulpanCd: r.get('chulpanCd'),
                sujumCd: r.get('sujumCd'),
                rowSeq: r.get('rowSeq')
            }));

            Ext.Ajax.request({
                url: dream.util.Common.BASE_URL + '/tbas/1004/delete',
                method: 'POST',
                jsonData: payload,
                success: function() {
                    Ext.Msg.alert('성공', '삭제되었습니다.');
                    store.remove(selection); // 로컬에서도 제거
                },
                failure: function() {
                    Ext.Msg.alert('오류', '삭제에 실패했습니다.');
                }
            });
        });
    },

